import React, { useState, useEffect } from 'react';
import { getRandomWords } from '../data/words';
import { Card as CardType, GameState } from '../types';
import Card from './Card';
import Timer from './Timer';

const Game: React.FC = () => {
  const [gameState, setGameState] = useState<GameState>({
    cards: [],
    flippedCards: [],
    isGameComplete: false,
    startTime: null,
    endTime: null,
  });

  const initializeGame = () => {
    const selectedWords = getRandomWords(12);
    const cards: CardType[] = selectedWords.map((word, index) => ({
      ...word,
      id: index,
      isFlipped: false,
      isMatched: false,
    }));

    setGameState({
      cards,
      flippedCards: [],
      isGameComplete: false,
      startTime: Date.now(),
      endTime: null,
    });
  };

  useEffect(() => {
    initializeGame();
  }, []);

  const handleCardClick = (clickedCard: CardType) => {
    if (
      gameState.flippedCards.length === 2 ||
      clickedCard.isFlipped ||
      clickedCard.isMatched
    ) {
      return;
    }

    const newCards = gameState.cards.map((card) =>
      card.id === clickedCard.id ? { ...card, isFlipped: true } : card
    );

    const newFlippedCards = [...gameState.flippedCards, clickedCard];

    setGameState((prev) => ({
      ...prev,
      cards: newCards,
      flippedCards: newFlippedCards,
    }));

    if (newFlippedCards.length === 2) {
      const [firstCard, secondCard] = newFlippedCards;
      const isMatch = firstCard.english === secondCard.english && 
                     firstCard.chinese === secondCard.chinese;

      if (isMatch) {
        const matchedCards = newCards.map((card) =>
          card.id === firstCard.id || card.id === secondCard.id
            ? { ...card, isMatched: true }
            : card
        );

        const isGameComplete = matchedCards.every((card) => card.isMatched);

        setGameState((prev) => ({
          ...prev,
          cards: matchedCards,
          flippedCards: [],
          isGameComplete,
          endTime: isGameComplete ? Date.now() : null,
        }));
      } else {
        setTimeout(() => {
          const resetCards = newCards.map((card) =>
            card.id === firstCard.id || card.id === secondCard.id
              ? { ...card, isFlipped: false }
              : card
          );

          setGameState((prev) => ({
            ...prev,
            cards: resetCards,
            flippedCards: [],
          }));
        }, 1000);
      }
    }
  };

  return (
    <div className="min-h-screen bg-gradient-to-b from-gray-100 to-gray-200 py-8">
      <div className="max-w-4xl mx-auto px-4">
        <div className="flex justify-between items-center mb-8">
          <Timer
            startTime={gameState.startTime}
            endTime={gameState.endTime}
          />
          <button
            onClick={initializeGame}
            className="px-6 py-3 bg-blue-500 text-white rounded-lg hover:bg-blue-600 
              transition-colors duration-200 transform hover:scale-105 focus:outline-none 
              focus:ring-2 focus:ring-blue-400 focus:ring-opacity-50 shadow-lg"
          >
            重新开始
          </button>
        </div>

        <div className="grid grid-cols-2 sm:grid-cols-3 md:grid-cols-4 lg:grid-cols-6 gap-4">
          {gameState.cards.map((card) => (
            <Card
              key={card.id}
              card={card}
              onClick={() => handleCardClick(card)}
            />
          ))}
        </div>

        {gameState.isGameComplete && (
          <div className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center">
            <div className="bg-white p-8 rounded-lg text-center transform transition-all duration-300 scale-100">
              <h2 className="text-3xl font-bold mb-4 text-blue-600">恭喜你完成游戏！</h2>
              <p className="text-xl mb-6 text-gray-700">
                用时：{Math.floor((gameState.endTime! - gameState.startTime!) / 1000)} 秒
              </p>
              <button
                onClick={initializeGame}
                className="px-6 py-3 bg-blue-500 text-white rounded-lg hover:bg-blue-600 
                  transition-all duration-200 transform hover:scale-105"
              >
                再玩一次
              </button>
            </div>
          </div>
        )}
      </div>
    </div>
  );
};

export default Game;
