import React from 'react';

interface GroupSelectionProps {
  onSelect: (group: number) => void;
}

const GroupSelection: React.FC<GroupSelectionProps> = ({ onSelect }) => {
  const groups = Array.from({ length: 8 }, (_, i) => i + 1);

  return (
    <div className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center">
      <div className="bg-white p-8 rounded-lg text-center max-w-md w-full mx-4">
        <h2 className="text-2xl font-bold mb-6 text-blue-600">选择你的组别</h2>
        <div className="grid grid-cols-2 sm:grid-cols-4 gap-4">
          {groups.map((group) => (
            <button
              key={group}
              onClick={() => onSelect(group)}
              className="p-4 bg-blue-500 text-white rounded-lg hover:bg-blue-600 
                transition-all duration-200 transform hover:scale-105"
            >
              第 {group} 组
            </button>
          ))}
        </div>
      </div>
    </div>
  );
};

export default GroupSelection;
