import React, { useState, useEffect } from 'react';
import Game from './components/Game';
import GroupSelection from './components/GroupSelection';
import AdminLogin from './components/AdminLogin';
import AdminPanel from './components/AdminPanel';
import GroupLeaderboard from './components/GroupLeaderboard';
import { Score } from './types';

const ADMIN_PASSWORD = 'admin123'; // In a real app, this should be stored securely
const SCORES_STORAGE_KEY = 'gameScores';

const App: React.FC = () => {
  const [selectedGroup, setSelectedGroup] = useState<number | null>(null);
  const [showAdminLogin, setShowAdminLogin] = useState(false);
  const [isAdmin, setIsAdmin] = useState(false);
  const [scores, setScores] = useState<Score[]>([]);

  // 从 localStorage 加载成绩
  const loadScores = () => {
    try {
      const savedScores = localStorage.getItem(SCORES_STORAGE_KEY);
      if (savedScores) {
        const parsedScores = JSON.parse(savedScores);
        if (Array.isArray(parsedScores)) {
          setScores(parsedScores);
        } else {
          console.error('Invalid scores data format');
          setScores([]);
        }
      }
    } catch (error) {
      console.error('Error loading scores:', error);
      setScores([]);
    }
  };

  // 保存成绩到 localStorage
  const saveScores = (updatedScores: Score[]) => {
    try {
      localStorage.setItem(SCORES_STORAGE_KEY, JSON.stringify(updatedScores));
      setScores(updatedScores);
    } catch (error) {
      console.error('Error saving scores:', error);
      // 可以在这里添加用户提示
    }
  };

  useEffect(() => {
    loadScores();
  }, []);

  const handleGroupSelect = (group: number) => {
    setSelectedGroup(group);
  };

  const handleGameComplete = (time: number) => {
    if (!selectedGroup) return;

    try {
      const newScore: Score = {
        group: selectedGroup,
        time,
        date: new Date().toISOString()
      };

      const updatedScores = [...scores, newScore];
      saveScores(updatedScores);
      setSelectedGroup(null);
    } catch (error) {
      console.error('Error handling game completion:', error);
      // 可以在这里添加用户提示
    }
  };

  const handleAdminLogin = (password: string) => {
    if (password === ADMIN_PASSWORD) {
      setIsAdmin(true);
      setShowAdminLogin(false);
      // 重新加载成绩以确保数据最新
      loadScores();
    }
  };

  const handleClearScores = () => {
    try {
      localStorage.removeItem(SCORES_STORAGE_KEY);
      setScores([]);
    } catch (error) {
      console.error('Error clearing scores:', error);
      // 可以在这里添加用户提示
    }
  };

  const handleAdminLogout = () => {
    setIsAdmin(false);
  };

  if (isAdmin) {
    return (
      <AdminPanel
        scores={scores}
        onClearScores={handleClearScores}
        onLogout={handleAdminLogout}
      />
    );
  }

  return (
    <div className="min-h-screen bg-gray-100">
      {showAdminLogin && (
        <AdminLogin
          onLogin={handleAdminLogin}
          onCancel={() => setShowAdminLogin(false)}
        />
      )}

      <div className="max-w-4xl mx-auto px-4 py-8">
        <div className="flex justify-between items-center mb-8">
          <h1 className="text-3xl font-bold text-blue-600">记忆翻牌游戏</h1>
          <button
            onClick={() => setShowAdminLogin(true)}
            className="px-4 py-2 text-sm text-gray-600 hover:text-gray-800"
          >
            管理员入口
          </button>
        </div>

        {!selectedGroup ? (
          <>
            <GroupSelection onSelect={handleGroupSelect} />
            {selectedGroup && (
              <GroupLeaderboard
                group={selectedGroup}
                scores={scores}
              />
            )}
          </>
        ) : (
          <Game
            group={selectedGroup}
            onComplete={handleGameComplete}
          />
        )}
      </div>
    </div>
  );
};

export default App;
