import React, { useState } from 'react';
import { Score } from '../types';

interface AdminPanelProps {
  scores: Score[];
  onClearScores: () => void;
  onLogout: () => void;
}

const AdminPanel: React.FC<AdminPanelProps> = ({ scores, onClearScores, onLogout }) => {
  const [selectedGroup, setSelectedGroup] = useState<number | 'all'>('all');

  // 按组别筛选成绩
  const filteredScores = selectedGroup === 'all' 
    ? scores 
    : scores.filter(score => score.group === selectedGroup);

  // 按组别统计平均用时
  const groupAverages = scores.reduce((acc, score) => {
    if (!acc[score.group]) {
      acc[score.group] = { total: 0, count: 0 };
    }
    acc[score.group].total += score.time;
    acc[score.group].count += 1;
    return acc;
  }, {} as Record<number, { total: number; count: number; }>);

  const handleClearScores = () => {
    if (window.confirm('确定要清空所有成绩记录吗？此操作不可撤销。')) {
      onClearScores();
    }
  };

  return (
    <div className="min-h-screen bg-gray-100 py-8">
      <div className="max-w-6xl mx-auto px-4">
        <div className="bg-white rounded-lg shadow-lg p-6">
          <div className="flex justify-between items-center mb-6">
            <h1 className="text-2xl font-bold text-blue-600">管理员面板</h1>
            <button
              onClick={onLogout}
              className="px-4 py-2 bg-gray-500 text-white rounded-lg hover:bg-gray-600 
                transition-colors duration-200"
            >
              退出登录
            </button>
          </div>

          <div className="mb-6">
            <h2 className="text-xl font-semibold mb-4">数据统计</h2>
            <div className="grid grid-cols-1 sm:grid-cols-2 md:grid-cols-4 gap-4">
              <div className="bg-blue-50 p-4 rounded-lg">
                <div className="text-sm text-gray-600">总完成次数</div>
                <div className="text-2xl font-bold text-blue-600">{scores.length}</div>
              </div>
              {Object.entries(groupAverages).map(([group, data]) => (
                <div key={group} className="bg-green-50 p-4 rounded-lg">
                  <div className="text-sm text-gray-600">第 {group} 组平均用时</div>
                  <div className="text-2xl font-bold text-green-600">
                    {Math.round(data.total / data.count)} 秒
                  </div>
                </div>
              ))}
            </div>
          </div>

          <div className="mb-6">
            <div className="flex justify-between items-center mb-4">
              <div className="flex items-center gap-4">
                <h2 className="text-xl font-semibold">成绩记录</h2>
                <select
                  value={selectedGroup}
                  onChange={(e) => setSelectedGroup(e.target.value === 'all' ? 'all' : Number(e.target.value))}
                  className="px-3 py-1 border rounded-lg focus:outline-none focus:ring-2 focus:ring-blue-400"
                >
                  <option value="all">所有组别</option>
                  {Array.from({ length: 8 }, (_, i) => i + 1).map(group => (
                    <option key={group} value={group}>第 {group} 组</option>
                  ))}
                </select>
              </div>
              <button
                onClick={handleClearScores}
                className="px-4 py-2 bg-red-500 text-white rounded-lg hover:bg-red-600 
                  transition-colors duration-200"
              >
                清空记录
              </button>
            </div>
            <div className="overflow-x-auto">
              <table className="min-w-full">
                <thead>
                  <tr className="bg-gray-100">
                    <th className="px-4 py-2 text-left">组别</th>
                    <th className="px-4 py-2 text-left">用时</th>
                    <th className="px-4 py-2 text-left">完成时间</th>
                  </tr>
                </thead>
                <tbody>
                  {filteredScores.map((score, index) => (
                    <tr 
                      key={`${score.group}-${score.date}`}
                      className={index % 2 === 0 ? 'bg-white' : 'bg-gray-50'}
                    >
                      <td className="px-4 py-2">第 {score.group} 组</td>
                      <td className="px-4 py-2">{score.time} 秒</td>
                      <td className="px-4 py-2">{new Date(score.date).toLocaleString()}</td>
                    </tr>
                  ))}
                </tbody>
              </table>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default AdminPanel;
