import React, { useState, useEffect } from 'react';
import Game from './components/Game';
import GroupSelection from './components/GroupSelection';
import AdminLogin from './components/AdminLogin';
import AdminPanel from './components/AdminPanel';
import { Score } from './types';

const ADMIN_PASSWORD = 'admin123'; // In a real app, this should be stored securely

const App: React.FC = () => {
  const [selectedGroup, setSelectedGroup] = useState<number | null>(null);
  const [showAdminLogin, setShowAdminLogin] = useState(false);
  const [isAdmin, setIsAdmin] = useState(false);
  const [scores, setScores] = useState<Score[]>([]);

  useEffect(() => {
    // Load scores from localStorage
    const savedScores = localStorage.getItem('gameScores');
    if (savedScores) {
      setScores(JSON.parse(savedScores));
    }
  }, []);

  const handleGroupSelect = (group: number) => {
    setSelectedGroup(group);
  };

  const handleGameComplete = (time: number) => {
    if (!selectedGroup) return;

    const newScore: Score = {
      group: selectedGroup,
      time,
      date: Date.now()
    };

    const updatedScores = [...scores, newScore];
    setScores(updatedScores);
    localStorage.setItem('gameScores', JSON.stringify(updatedScores));
    setSelectedGroup(null);
  };

  const handleAdminLogin = (password: string) => {
    if (password === ADMIN_PASSWORD) {
      setIsAdmin(true);
      setShowAdminLogin(false);
    }
  };

  const handleClearScores = () => {
    setScores([]);
    localStorage.removeItem('gameScores');
  };

  const handleAdminLogout = () => {
    setIsAdmin(false);
  };

  if (isAdmin) {
    return (
      <AdminPanel
        scores={scores}
        onClearScores={handleClearScores}
        onLogout={handleAdminLogout}
      />
    );
  }

  return (
    <div className="min-h-screen bg-gray-100">
      {showAdminLogin && (
        <AdminLogin
          onLogin={handleAdminLogin}
          onClose={() => setShowAdminLogin(false)}
        />
      )}

      {!selectedGroup ? (
        <div className="max-w-4xl mx-auto px-4 py-8">
          <div className="flex justify-between items-center mb-8">
            <h1 className="text-3xl font-bold text-blue-600">记忆翻牌游戏</h1>
            <button
              onClick={() => setShowAdminLogin(true)}
              className="px-4 py-2 text-sm text-gray-600 hover:text-gray-800"
            >
              管理员入口
            </button>
          </div>
          <GroupSelection onSelect={handleGroupSelect} />
        </div>
      ) : (
        <Game
          group={selectedGroup}
          onComplete={handleGameComplete}
        />
      )}
    </div>
  );
};

export default App;
