import React from 'react';
import { motion } from 'framer-motion';
import { Card as CardType } from '../types';

interface CardProps {
  card: CardType;
  onClick: () => void;
}

const Card: React.FC<CardProps> = ({ card, onClick }) => {
  return (
    <motion.div
      className="relative w-32 h-40 cursor-pointer perspective-1000"
      onClick={onClick}
      animate={{ rotateY: card.isFlipped ? 180 : 0 }}
      transition={{ duration: 0.6 }}
    >
      <div className="relative w-full h-full preserve-3d">
        {/* 正面 */}
        <div className="absolute w-full h-full backface-hidden bg-blue-500 rounded-lg shadow-lg flex items-center justify-center text-white text-xl font-bold">
          ?
        </div>

        {/* 背面 */}
        <div className="absolute w-full h-full backface-hidden bg-white rounded-lg shadow-lg flex items-center justify-center p-4 rotate-y-180">
          <div className="text-center">
            <div className="text-lg font-bold text-gray-800">{card.english}</div>
            <div className="text-sm text-gray-600 mt-2">{card.chinese}</div>
          </div>
        </div>
      </div>
    </motion.div>
  );
};

export default Card;
