import React from 'react';
import { motion } from 'framer-motion';
import { Card as CardType } from '../types';

interface CardProps {
  card: CardType;
  isFlipped: boolean;
  isDisabled: boolean;
  onClick: () => void;
}

const Card: React.FC<CardProps> = ({ card, isFlipped, isDisabled, onClick }) => {
  return (
    <motion.div
      className={`relative w-full aspect-square cursor-pointer ${
        isDisabled ? 'pointer-events-none' : ''
      }`}
      whileHover={{ scale: isDisabled ? 1 : 1.05 }}
      onClick={onClick}
    >
      <motion.div
        className="absolute inset-0 rounded-lg"
        initial={false}
        animate={{
          rotateY: isFlipped ? 180 : 0
        }}
        transition={{ duration: 0.6 }}
        style={{
          transformStyle: 'preserve-3d'
        }}
      >
        {/* 卡片正面 */}
        <div
          className={`absolute inset-0 flex items-center justify-center p-4 
            rounded-lg bg-white shadow-lg text-center
            ${card.type === 'english' ? 'text-blue-600' : 'text-green-600'}
            ${isFlipped ? 'backface-hidden' : ''}`}
        >
          <span className="text-xl font-bold">?</span>
        </div>

        {/* 卡片背面 */}
        <div
          className={`absolute inset-0 flex items-center justify-center p-4 
            rounded-lg shadow-lg text-center transform rotate-y-180
            ${card.type === 'english' ? 'bg-blue-500 text-white' : 'bg-green-500 text-white'}
            ${!isFlipped ? 'backface-hidden' : ''}`}
        >
          <span className={`text-lg font-bold ${
            card.type === 'chinese' ? 'text-2xl' : ''
          }`}>
            {card.content}
          </span>
        </div>
      </motion.div>
    </motion.div>
  );
};

export default Card;
