import React from 'react';
import { motion } from 'framer-motion';
import { Card as CardType } from '../types';

interface CardProps {
  card: CardType;
  isFlipped: boolean;
  isDisabled: boolean;
  onClick: () => void;
}

const Card: React.FC<CardProps> = ({ card, isFlipped, isDisabled, onClick }) => {
  return (
    <motion.div
      className={`relative w-full aspect-square cursor-pointer ${
        isDisabled ? 'pointer-events-none' : ''
      }`}
      whileHover={{ scale: isDisabled ? 1 : 1.05 }}
      onClick={onClick}
    >
      <motion.div
        className="absolute inset-0 rounded-lg"
        initial={false}
        animate={{
          rotateY: isFlipped ? 180 : 0
        }}
        transition={{ duration: 0.6 }}
        style={{
          transformStyle: 'preserve-3d'
        }}
      >
        {/* 卡片正面（问号面） */}
        <div
          className="absolute inset-0 flex items-center justify-center p-4 
            rounded-lg bg-white shadow-lg text-center"
          style={{
            backfaceVisibility: 'hidden'
          }}
        >
          <span className="text-xl font-bold text-gray-600">?</span>
        </div>

        {/* 卡片背面（内容面） */}
        <div
          className={`absolute inset-0 flex items-center justify-center p-4 
            rounded-lg shadow-lg text-center
            ${card.type === 'english' ? 'bg-blue-500' : 'bg-green-500'}`}
          style={{
            backfaceVisibility: 'hidden',
            transform: 'rotateY(180deg)'
          }}
        >
          <span 
            className={`font-bold text-white
              ${card.type === 'english' ? 'text-xl' : 'text-2xl'}`}
            style={{
              transform: 'rotateY(180deg)'
            }}
          >
            {isFlipped ? card.content : ''}
          </span>
        </div>
      </motion.div>
    </motion.div>
  );
};

export default Card;
