import React from 'react';
import { Score } from '../types';

interface LeaderboardProps {
  scores: Score[];
  groupNumber?: number; // ÂèØÈÄâÂèÇÊï∞,Â¶ÇÊûúÊèê‰æõÂàôÂè™ÊòæÁ§∫ËØ•ÁªÑÁöÑÊàêÁª©
  showGroupColumn?: boolean; // ÊòØÂê¶ÊòæÁ§∫ÁªÑÂà´Âàó
}

const Leaderboard: React.FC<LeaderboardProps> = ({ 
  scores, 
  groupNumber,
  showGroupColumn = true 
}) => {
  // Â¶ÇÊûúÊåáÂÆö‰∫ÜÁªÑÂè∑,Âè™ÊòæÁ§∫ËØ•ÁªÑÁöÑÊàêÁª©
  const filteredScores = groupNumber 
    ? scores.filter(score => score.group === groupNumber)
    : scores;

  // ÊåâÂÆåÊàêÊó∂Èó¥ÊéíÂ∫è
  const sortedScores = [...filteredScores].sort((a, b) => a.time - b.time);

  return (
    <div className="bg-white rounded-lg shadow-lg p-6">
      <h2 className="text-xl font-bold text-center text-blue-600 mb-4">
        {groupNumber ? `Á¨¨ ${groupNumber} ÁªÑÊéíË°åÊ¶ú` : 'ÊÄªÊéíË°åÊ¶ú'}
      </h2>
      {sortedScores.length > 0 ? (
        <div className="overflow-x-auto">
          <table className="min-w-full">
            <thead>
              <tr className="bg-gray-50">
                <th className="px-4 py-2 text-left">ÊéíÂêç</th>
                {showGroupColumn && (
                  <th className="px-4 py-2 text-left">ÁªÑÂà´</th>
                )}
                <th className="px-4 py-2 text-left">Áî®Êó∂</th>
                <th className="px-4 py-2 text-left">ÂÆåÊàêÊó∂Èó¥</th>
              </tr>
            </thead>
            <tbody>
              {sortedScores.map((score, index) => (
                <tr 
                  key={`${score.group}-${score.date}`}
                  className={index % 2 === 0 ? 'bg-white' : 'bg-gray-50'}
                >
                  <td className="px-4 py-2">
                    {index === 0 && 'ü•á'}
                    {index === 1 && 'ü•à'}
                    {index === 2 && 'ü•â'}
                    {index > 2 && (index + 1)}
                  </td>
                  {showGroupColumn && (
                    <td className="px-4 py-2">Á¨¨ {score.group} ÁªÑ</td>
                  )}
                  <td className="px-4 py-2">{score.time} Áßí</td>
                  <td className="px-4 py-2">
                    {new Date(score.date).toLocaleString()}
                  </td>
                </tr>
              ))}
            </tbody>
          </table>
        </div>
      ) : (
        <p className="text-center text-gray-500">ÊöÇÊó†ÊàêÁª©ËÆ∞ÂΩï</p>
      )}
    </div>
  );
};

export default Leaderboard;
